WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year IN (1999, 2000, 2001) AND (d_dom BETWEEN 1 AND 3 OR d_dom BETWEEN 25 AND 28)), filtered_store AS (SELECT s_store_sk FROM store WHERE s_county = 'Williamson County'), filtered_hd AS (SELECT hd_demo_sk, hd_buy_potential, hd_dep_count, hd_vehicle_count FROM household_demographics WHERE hd_vehicle_count > 0 AND (hd_buy_potential = '>10000' OR hd_buy_potential = 'Unknown') AND (hd_dep_count * 1.000) / hd_vehicle_count > 1.2), store_sales_branch1 AS (SELECT ss_ticket_number, ss_customer_sk, 1 AS sales_count FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 1 AND 3 AND hd_buy_potential = '>10000'), store_sales_branch2 AS (SELECT ss_ticket_number, ss_customer_sk, 1 AS sales_count FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 1 AND 3 AND hd_buy_potential = 'Unknown'), store_sales_branch3 AS (SELECT ss_ticket_number, ss_customer_sk, 1 AS sales_count FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 25 AND 28 AND hd_buy_potential = '>10000'), store_sales_branch4 AS (SELECT ss_ticket_number, ss_customer_sk, 1 AS sales_count FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 25 AND 28 AND hd_buy_potential = 'Unknown'), combined_sales AS (SELECT ss_ticket_number, ss_customer_sk, SUM(sales_count) AS cnt FROM (SELECT * FROM store_sales_branch1 UNION ALL SELECT * FROM store_sales_branch2 UNION ALL SELECT * FROM store_sales_branch3 UNION ALL SELECT * FROM store_sales_branch4) AS all_sales GROUP BY ss_ticket_number, ss_customer_sk)
SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM combined_sales JOIN customer ON ss_customer_sk = c_customer_sk WHERE cnt BETWEEN 15 AND 20 ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, c_salutation NULLS FIRST, c_preferred_cust_flag DESC NULLS FIRST, ss_ticket_number NULLS FIRST