WITH eligible_manufacturers AS (SELECT DISTINCT i_manufact FROM (SELECT i_manufact FROM item WHERE i_category = 'Women' AND i_color IN ('powder', 'khaki') AND i_units IN ('Ounce', 'Oz') AND i_size IN ('medium', 'extra large') UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Women' AND i_color IN ('brown', 'honeydew') AND i_units IN ('Bunch', 'Ton') AND i_size IN ('N/A', 'small') UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Men' AND i_color IN ('floral', 'deep') AND i_units IN ('N/A', 'Dozen') AND i_size = 'petite' UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Men' AND i_color IN ('light', 'cornflower') AND i_units IN ('Box', 'Pound') AND i_size IN ('medium', 'extra large') UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Women' AND i_color IN ('midnight', 'snow') AND i_units IN ('Pallet', 'Gross') AND i_size IN ('medium', 'extra large') UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Women' AND i_color IN ('cyan', 'papaya') AND i_units IN ('Cup', 'Dram') AND i_size IN ('N/A', 'small') UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Men' AND i_color IN ('orange', 'frosted') AND i_units IN ('Each', 'Tbl') AND i_size = 'petite' UNION ALL SELECT i_manufact FROM item WHERE i_category = 'Men' AND i_color IN ('forest', 'ghost') AND i_units IN ('Lb', 'Bundle') AND i_size IN ('medium', 'extra large')) AS t)
SELECT DISTINCT i_product_name FROM item AS i1 WHERE i_manufact_id BETWEEN 738 AND 738 + 40 AND EXISTS(SELECT 1 FROM eligible_manufacturers AS em WHERE em.i_manufact = i1.i_manufact) ORDER BY i_product_name LIMIT 100