WITH date_range AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1200 AND 1200 + 11), filtered_sales AS (SELECT ss_net_profit, ss_store_sk, ss_sold_date_sk FROM store_sales), sales_with_date AS (SELECT fss.ss_net_profit, fss.ss_store_sk, s.s_state, s.s_county FROM filtered_sales AS fss JOIN date_range AS dr ON fss.ss_sold_date_sk = dr.d_date_sk JOIN store AS s ON fss.ss_store_sk = s.s_store_sk), state_ranking AS (SELECT s_state, RANK() OVER (ORDER BY SUM(ss_net_profit) DESC) AS ranking FROM sales_with_date GROUP BY s_state), top_states AS (SELECT s_state FROM state_ranking WHERE ranking <= 5)
SELECT SUM(ss_net_profit) AS total_sum, s_state, s_county, GROUPING(s_state) + GROUPING(s_county) AS lochierarchy, RANK() OVER (PARTITION BY GROUPING(s_state) + GROUPING(s_county), CASE WHEN GROUPING(s_county) = 0 THEN s_state END ORDER BY SUM(ss_net_profit) DESC) AS rank_within_parent FROM sales_with_date WHERE s_state IN (SELECT s_state FROM top_states) GROUP BY ROLLUP (s_state, s_county) ORDER BY lochierarchy DESC, CASE WHEN GROUPING(s_state) + GROUPING(s_county) = 0 THEN s_state END, rank_within_parent LIMIT 100