WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1200 AND 1200 + 11), filtered_sales AS (SELECT ws_item_sk, ws_net_paid FROM web_sales AS ws JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk), aggregated_sales AS (SELECT i.i_category, i.i_class, SUM(fs.ws_net_paid) AS item_total FROM filtered_sales AS fs JOIN item AS i ON fs.ws_item_sk = i.i_item_sk GROUP BY i.i_category, i.i_class)
SELECT SUM(item_total) AS total_sum, i_category, i_class, GROUPING(i_category) + GROUPING(i_class) AS lochierarchy, RANK() OVER (PARTITION BY GROUPING(i_category) + GROUPING(i_class), CASE WHEN GROUPING(i_class) = 0 THEN i_category END ORDER BY SUM(item_total) DESC) AS rank_within_parent FROM aggregated_sales GROUP BY ROLLUP (i_category, i_class) ORDER BY lochierarchy DESC NULLS FIRST, CASE WHEN GROUPING(i_category) + GROUPING(i_class) = 0 THEN i_category END NULLS FIRST, rank_within_parent NULLS FIRST LIMIT 100