WITH filtered_dates AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_month_seq BETWEEN 1200 AND 1200 + 11), store_sales_set AS (SELECT DISTINCT c.c_last_name, c.c_first_name, fd.d_date FROM store_sales AS ss JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN customer AS c ON ss.ss_customer_sk = c.c_customer_sk), catalog_sales_set AS (SELECT DISTINCT c.c_last_name, c.c_first_name, fd.d_date FROM catalog_sales AS cs JOIN filtered_dates AS fd ON cs.cs_sold_date_sk = fd.d_date_sk JOIN customer AS c ON cs.cs_bill_customer_sk = c.c_customer_sk), web_sales_set AS (SELECT DISTINCT c.c_last_name, c.c_first_name, fd.d_date FROM web_sales AS ws JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk JOIN customer AS c ON ws.ws_bill_customer_sk = c.c_customer_sk)
SELECT COUNT(*) FROM ((SELECT * FROM store_sales_set) EXCEPT (SELECT * FROM catalog_sales_set) EXCEPT (SELECT * FROM web_sales_set)) AS cool_cust