WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000 AND d_moy = 2), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -6), filtered_items AS (SELECT i_item_sk, i_item_id FROM item WHERE i_color IN ('powder', 'green', 'cyan')), ss AS (SELECT fi.i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales AS ss JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_customer_address AS fca ON ss.ss_addr_sk = fca.ca_address_sk JOIN filtered_items AS fi ON ss.ss_item_sk = fi.i_item_sk GROUP BY fi.i_item_id), ws AS (SELECT fi.i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales AS ws JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk JOIN filtered_customer_address AS fca ON ws.ws_bill_addr_sk = fca.ca_address_sk JOIN filtered_items AS fi ON ws.ws_item_sk = fi.i_item_sk GROUP BY fi.i_item_id), cs AS (SELECT i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales, date_dim, customer_address, item WHERE i_item_id IN (SELECT i_item_id FROM item WHERE i_color IN ('powder', 'green', 'cyan')) AND cs_item_sk = i_item_sk AND cs_sold_date_sk = d_date_sk AND d_year = 2000 AND d_moy = 2 AND cs_bill_addr_sk = ca_address_sk AND ca_gmt_offset = -6 GROUP BY i_item_id)
SELECT i_item_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_item_id ORDER BY total_sales, i_item_id LIMIT 100