WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-2-01' AND (CAST('2000-2-01' AS DATE) + INTERVAL '60' DAY)), filtered_web_sales AS (SELECT ws1.ws_order_number, ws1.ws_ext_ship_cost, ws1.ws_net_profit, ws1.ws_warehouse_sk, ws1.ws_ship_addr_sk, ws1.ws_web_site_sk FROM web_sales AS ws1 JOIN filtered_dates ON ws1.ws_ship_date_sk = filtered_dates.d_date_sk), multi_warehouse_orders AS (SELECT ws_order_number FROM web_sales GROUP BY ws_order_number HAVING COUNT(DISTINCT ws_warehouse_sk) > 1), returned_orders AS (SELECT DISTINCT wr_order_number FROM web_returns)
SELECT COUNT(DISTINCT fws.ws_order_number) AS "order count", SUM(fws.ws_ext_ship_cost) AS "total shipping cost", SUM(fws.ws_net_profit) AS "total net profit" FROM filtered_web_sales AS fws JOIN customer_address ON fws.ws_ship_addr_sk = ca_address_sk JOIN web_site ON fws.ws_web_site_sk = web_site_sk JOIN multi_warehouse_orders AS mwo ON fws.ws_order_number = mwo.ws_order_number LEFT JOIN returned_orders AS ro ON fws.ws_order_number = ro.wr_order_number WHERE ca_state = 'OK' AND web_company_name = 'pri' AND ro.wr_order_number IS NULL GROUP BY 1 ORDER BY COUNT(DISTINCT fws.ws_order_number) LIMIT 100