WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1999-2-01' AND (CAST('1999-2-01' AS DATE) + INTERVAL '60' DAY)), ws_wh AS (SELECT ws1.ws_order_number, ws1.ws_warehouse_sk AS wh1, ws2.ws_warehouse_sk AS wh2 FROM web_sales AS ws1, web_sales AS ws2 WHERE ws1.ws_order_number = ws2.ws_order_number AND ws1.ws_warehouse_sk <> ws2.ws_warehouse_sk), multi_warehouse_orders AS (SELECT DISTINCT ws_order_number FROM ws_wh), returned_multi_warehouse_orders AS (SELECT DISTINCT wr_order_number FROM web_returns AS wr JOIN ws_wh ON wr.wr_order_number = ws_wh.ws_order_number)
SELECT COUNT(DISTINCT ws1.ws_order_number) AS "order count", SUM(ws1.ws_ext_ship_cost) AS "total shipping cost", SUM(ws1.ws_net_profit) AS "total net profit" FROM web_sales AS ws1 JOIN filtered_dates AS fd ON ws1.ws_ship_date_sk = fd.d_date_sk JOIN customer_address AS ca ON ws1.ws_ship_addr_sk = ca.ca_address_sk JOIN web_site AS ws ON ws1.ws_web_site_sk = ws.web_site_sk JOIN multi_warehouse_orders AS mwo ON ws1.ws_order_number = mwo.ws_order_number JOIN returned_multi_warehouse_orders AS rmwo ON ws1.ws_order_number = rmwo.wr_order_number WHERE ca.ca_state = 'NC' AND ws.web_company_name = 'pri' ORDER BY COUNT(DISTINCT ws1.ws_order_number) LIMIT 100