WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'F' AND cd_marital_status = 'D' AND cd_education_status = 'Secondary'), filtered_stores AS (SELECT s_store_sk, s_state FROM store WHERE s_state IN ('MO', 'AL', 'MI', 'TN', 'LA', 'SC')), filtered_items AS (SELECT i_item_sk, i_item_id FROM item), joined_facts AS (SELECT ss_item_sk, ss_store_sk, ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price FROM store_sales AS ss JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_customer_demographics AS fcd ON ss.ss_cdemo_sk = fcd.cd_demo_sk)
SELECT i_item_id, s_state, GROUPING(s_state) AS g_state, AVG(ss_quantity) AS agg1, AVG(ss_list_price) AS agg2, AVG(ss_coupon_amt) AS agg3, AVG(ss_sales_price) AS agg4 FROM joined_facts AS jf JOIN filtered_stores AS fs ON jf.ss_store_sk = fs.s_store_sk JOIN filtered_items AS fi ON jf.ss_item_sk = fi.i_item_sk GROUP BY ROLLUP (i_item_id, s_state) ORDER BY i_item_id, s_state LIMIT 100