WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002 AND d_moy = 1), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), home_items AS (SELECT i_manufact_id, i_item_sk FROM item WHERE i_category IN ('Home')), ss AS (SELECT hi.i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales AS ss JOIN home_items AS hi ON ss.ss_item_sk = hi.i_item_sk JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_addresses AS fa ON ss.ss_addr_sk = fa.ca_address_sk GROUP BY hi.i_manufact_id), ws AS (SELECT hi.i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales AS ws JOIN home_items AS hi ON ws.ws_item_sk = hi.i_item_sk JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk JOIN filtered_addresses AS fa ON ws.ws_bill_addr_sk = fa.ca_address_sk GROUP BY hi.i_manufact_id), cs AS (SELECT hi.i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales AS cs JOIN home_items AS hi ON cs.cs_item_sk = hi.i_item_sk JOIN filtered_dates AS fd ON cs.cs_sold_date_sk = fd.d_date_sk JOIN filtered_addresses AS fa ON cs.cs_bill_addr_sk = fa.ca_address_sk GROUP BY hi.i_manufact_id)
SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_manufact_id ORDER BY total_sales LIMIT 100