WITH filtered_dates AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_year = 2002 AND d_moy = 12), filtered_items AS (SELECT i_item_sk, i_brand_id, i_brand FROM item WHERE i_manager_id = 1)
SELECT fd.d_year, fi.i_brand_id AS brand_id, fi.i_brand AS brand, SUM(ss_ext_sales_price) AS ext_price FROM store_sales JOIN filtered_dates AS fd ON dt.d_date_sk = ss_sold_date_sk JOIN filtered_items AS fi ON ss_item_sk = i_item_sk GROUP BY fd.d_year, fi.i_brand, fi.i_brand_id ORDER BY fd.d_year, ext_price DESC, brand_id LIMIT 100