WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1999 AND d_moy = 11), filtered_stores AS (SELECT s_store_sk FROM store WHERE s_gmt_offset = -7), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7), filtered_items AS (SELECT i_item_sk FROM item WHERE i_category = 'Jewelry'), filtered_customers AS (SELECT c_customer_sk, c_current_addr_sk FROM customer), promotional_condition AS (SELECT p_promo_sk FROM promotion WHERE p_channel_dmail = 'Y' OR p_channel_email = 'Y' OR p_channel_tv = 'Y')
SELECT promotions, total, CAST(promotions AS DECIMAL(15, 4)) / CAST(total AS DECIMAL(15, 4)) * 100 FROM (SELECT SUM(CASE WHEN NOT pc.p_promo_sk IS NULL THEN ss.ss_ext_sales_price ELSE 0 END) AS promotions, SUM(ss.ss_ext_sales_price) AS total FROM store_sales AS ss JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_stores AS fs ON ss.ss_store_sk = fs.s_store_sk JOIN filtered_customers AS c ON ss.ss_customer_sk = c.c_customer_sk JOIN filtered_addresses AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN filtered_items AS i ON ss.ss_item_sk = i.i_item_sk LEFT JOIN promotional_condition AS pc ON ss.ss_promo_sk = pc.p_promo_sk) AS combined_sales ORDER BY promotions, total LIMIT 100