WITH cs_ui AS (SELECT cs_item_sk, SUM(cs_ext_list_price) AS sale, SUM(cr_refunded_cash + cr_reversed_charge + cr_store_credit) AS refund FROM catalog_sales, catalog_returns WHERE cs_item_sk = cr_item_sk AND cs_order_number = cr_order_number GROUP BY cs_item_sk HAVING SUM(cs_ext_list_price) > 2 * SUM(cr_refunded_cash + cr_reversed_charge + cr_store_credit)), d1_dates AS (SELECT d_date_sk, d_year FROM date_dim), d2_dates AS (SELECT d_date_sk, d_year FROM date_dim), d3_dates AS (SELECT d_date_sk, d_year FROM date_dim), filtered_store_sales AS (SELECT ss_item_sk, ss_customer_sk, ss_ticket_number, ss_store_sk, ss_wholesale_cost, ss_list_price, ss_coupon_amt, ss_cdemo_sk, ss_hdemo_sk, ss_addr_sk, ss_promo_sk FROM store_sales JOIN d1_dates ON store_sales.ss_sold_date_sk = d1_dates.d_date_sk), filtered_store_returns AS (SELECT sr_item_sk, sr_ticket_number FROM store_returns), filtered_customer AS (SELECT c_customer_sk, c_current_cdemo_sk, c_current_hdemo_sk, c_current_addr_sk, c_first_sales_date_sk, c_first_shipto_date_sk FROM customer), cross_sales AS (SELECT i_product_name AS product_name, i_item_sk AS item_sk, s_store_name AS store_name, s_zip AS store_zip, ad1.ca_street_number AS b_street_number, ad1.ca_street_name AS b_street_name, ad1.ca_city AS b_city, ad1.ca_zip AS b_zip, ad2.ca_street_number AS c_street_number, ad2.ca_street_name AS c_street_name, ad2.ca_city AS c_city, ad2.ca_zip AS c_zip, d1_dates.d_year AS syear, d2_dates.d_year AS fsyear, d3_dates.d_year AS s2year, COUNT(*) AS cnt, SUM(fss.ss_wholesale_cost) AS s1, SUM(fss.ss_list_price) AS s2, SUM(fss.ss_coupon_amt) AS s3 FROM filtered_store_sales AS fss JOIN filtered_store_returns AS fsr ON fss.ss_item_sk = fsr.sr_item_sk AND fss.ss_ticket_number = fsr.sr_ticket_number JOIN cs_ui ON fss.ss_item_sk = cs_ui.cs_item_sk JOIN d1_dates ON fss.ss_sold_date_sk = d1_dates.d_date_sk JOIN filtered_customer AS fc ON fss.ss_customer_sk = fc.c_customer_sk JOIN d2_dates ON fc.c_first_sales_date_sk = d2_dates.d_date_sk JOIN d3_dates ON fc.c_first_shipto_date_sk = d3_dates.d_date_sk JOIN store AS s ON fss.ss_store_sk = s.s_store_sk JOIN customer_demographics AS cd1 ON fss.ss_cdemo_sk = cd1.cd_demo_sk JOIN customer_demographics AS cd2 ON fc.c_current_cdemo_sk = cd2.cd_demo_sk JOIN promotion AS p ON fss.ss_promo_sk = p_promo_sk JOIN household_demographics AS hd1 ON fss.ss_hdemo_sk = hd1.hd_demo_sk JOIN household_demographics AS hd2 ON fc.c_current_hdemo_sk = hd2.hd_demo_sk JOIN customer_address AS ad1 ON fss.ss_addr_sk = ad1.ca_address_sk JOIN customer_address AS ad2 ON fc.c_current_addr_sk = ad2.ca_address_sk JOIN income_band AS ib1 ON hd1.hd_income_band_sk = ib1.ib_income_band_sk JOIN income_band AS ib2 ON hd2.hd_income_band_sk = ib2.ib_income_band_sk JOIN item AS i ON fss.ss_item_sk = i.i_item_sk WHERE cd1.cd_marital_status <> cd2.cd_marital_status AND i.i_color IN ('blanched', 'medium', 'brown', 'chocolate', 'burlywood', 'drab') AND i.i_current_price BETWEEN 23 AND 23 + 10 AND i.i_current_price BETWEEN 23 + 1 AND 23 + 15 GROUP BY i.i_product_name, i.i_item_sk, s.s_store_name, s.s_zip, ad1.ca_street_number, ad1.ca_street_name, ad1.ca_city, ad1.ca_zip, ad2.ca_street_number, ad2.ca_street_name, ad2.ca_city, ad2.ca_zip, d1_dates.d_year, d2_dates.d_year, d3_dates.d_year)
SELECT cs1.product_name, cs1.store_name, cs1.store_zip, cs1.b_street_number, cs1.b_street_name, cs1.b_city, cs1.b_zip, cs1.c_street_number, cs1.c_street_name, cs1.c_city, cs1.c_zip, cs1.syear, cs1.cnt, cs1.s1 AS s11, cs1.s2 AS s21, cs1.s3 AS s31, cs2.s1 AS s12, cs2.s2 AS s22, cs2.s3 AS s32, cs2.syear, cs2.cnt FROM cross_sales AS cs1, cross_sales AS cs2 WHERE cs1.item_sk = cs2.item_sk AND cs1.syear = 2001 AND cs2.syear = 2001 + 1 AND cs2.cnt <= cs1.cnt AND cs1.store_name = cs2.store_name AND cs1.store_zip = cs2.store_zip ORDER BY cs1.product_name, cs1.store_name, cs2.cnt, cs1.s1, cs2.s1