WITH filtered_d2 AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 8), filtered_returns AS (SELECT sr.sr_returned_date_sk, ss.ss_store_sk, ss.ss_sold_date_sk FROM store_returns AS sr JOIN filtered_d2 ON sr.sr_returned_date_sk = filtered_d2.d_date_sk JOIN store_sales AS ss ON sr.sr_ticket_number = ss.ss_ticket_number AND sr.sr_item_sk = ss.ss_item_sk AND sr.sr_customer_sk = ss.ss_customer_sk)
SELECT s.s_store_name, s.s_company_id, s.s_street_number, s.s_street_name, s.s_street_type, s.s_suite_number, s.s_city, s.s_county, s.s_state, s.s_zip, SUM(CASE WHEN (fr.sr_returned_date_sk - fr.ss_sold_date_sk <= 30) THEN 1 ELSE 0 END) AS "30 days", SUM(CASE WHEN (fr.sr_returned_date_sk - fr.ss_sold_date_sk > 30) AND (fr.sr_returned_date_sk - fr.ss_sold_date_sk <= 60) THEN 1 ELSE 0 END) AS "31-60 days", SUM(CASE WHEN (fr.sr_returned_date_sk - fr.ss_sold_date_sk > 60) AND (fr.sr_returned_date_sk - fr.ss_sold_date_sk <= 90) THEN 1 ELSE 0 END) AS "61-90 days", SUM(CASE WHEN (fr.sr_returned_date_sk - fr.ss_sold_date_sk > 90) AND (fr.sr_returned_date_sk - fr.ss_sold_date_sk <= 120) THEN 1 ELSE 0 END) AS "91-120 days", SUM(CASE WHEN (fr.sr_returned_date_sk - fr.ss_sold_date_sk > 120) THEN 1 ELSE 0 END) AS ">120 days" FROM filtered_returns AS fr JOIN store AS s ON fr.ss_store_sk = s.s_store_sk GROUP BY s.s_store_name, s.s_company_id, s.s_street_number, s.s_street_name, s.s_street_type, s.s_suite_number, s.s_city, s.s_county, s.s_state, s.s_zip ORDER BY s.s_store_name, s.s_company_id, s.s_street_number, s.s_street_name, s.s_street_type, s.s_suite_number, s.s_city, s.s_county, s.s_state, s.s_zip LIMIT 100