WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2002-02-01' AND CAST('2002-04-02' AS DATE)), filtered_shipments AS (SELECT cs1.cs_order_number, cs1.cs_ext_ship_cost, cs1.cs_net_profit, cs1.cs_ship_addr_sk, cs1.cs_call_center_sk FROM catalog_sales AS cs1 JOIN filtered_dates ON cs1.cs_ship_date_sk = d_date_sk)
SELECT COUNT(DISTINCT cs_order_number) AS "order count", SUM(cs_ext_ship_cost) AS "total shipping cost", SUM(cs_net_profit) AS "total net profit" FROM filtered_shipments AS fs JOIN customer_address ON fs.cs_ship_addr_sk = ca_address_sk JOIN call_center ON fs.cs_call_center_sk = cc_call_center_sk WHERE ca_state = 'GA' AND cc_county = 'Williamson County' AND EXISTS(SELECT 1 FROM catalog_sales AS cs2 WHERE fs.cs_order_number = cs2.cs_order_number AND fs.cs_ship_addr_sk <> cs2.cs_warehouse_sk) AND NOT EXISTS(SELECT 1 FROM catalog_returns AS cr1 WHERE fs.cs_order_number = cr1.cr_order_number) GROUP BY () ORDER BY COUNT(DISTINCT cs_order_number) LIMIT 100