WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), filtered_cd AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'S' AND cd_education_status = 'College'), email_promo AS (SELECT p_promo_sk FROM promotion WHERE p_channel_email = 'N'), event_promo AS (SELECT p_promo_sk FROM promotion WHERE p_channel_event = 'N'), branch1 AS (SELECT ss_item_sk, ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_cd ON ss_cdemo_sk = cd_demo_sk JOIN email_promo ON ss_promo_sk = p_promo_sk), branch2 AS (SELECT ss_item_sk, ss_quantity, ss_list_price, ss_coupon_amt, ss_sales_price FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_cd ON ss_cdemo_sk = cd_demo_sk JOIN event_promo ON ss_promo_sk = p_promo_sk), combined_sales AS (SELECT * FROM branch1 UNION ALL SELECT * FROM branch2)
SELECT i_item_id, AVG(ss_quantity) AS agg1, AVG(ss_list_price) AS agg2, AVG(ss_coupon_amt) AS agg3, AVG(ss_sales_price) AS agg4 FROM combined_sales AS s JOIN item AS i ON s.ss_item_sk = i.i_item_sk GROUP BY i_item_id ORDER BY i_item_id LIMIT 100