WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1999-02-01' AND CAST('1999-04-02' AS DATE)), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'IL'), filtered_site AS (SELECT web_site_sk FROM web_site WHERE web_company_name = 'pri'), base_sales AS (SELECT ws_order_number, ws_ext_ship_cost, ws_net_profit, ws_warehouse_sk FROM web_sales AS ws1 WHERE ws_ship_date_sk IN (SELECT d_date_sk FROM filtered_dates) AND ws_ship_addr_sk IN (SELECT ca_address_sk FROM filtered_address) AND ws_web_site_sk IN (SELECT web_site_sk FROM filtered_site)), orders_with_multiple_warehouses AS (SELECT ws_order_number FROM web_sales GROUP BY ws_order_number HAVING COUNT(DISTINCT ws_warehouse_sk) > 1), orders_with_returns AS (SELECT DISTINCT wr_order_number FROM web_returns)
SELECT COUNT(DISTINCT ws_order_number) AS "order count", SUM(ws_ext_ship_cost) AS "total shipping cost", SUM(ws_net_profit) AS "total net profit" FROM base_sales WHERE ws_order_number IN (SELECT ws_order_number FROM orders_with_multiple_warehouses) AND NOT ws_order_number IN (SELECT wr_order_number FROM orders_with_returns) ORDER BY COUNT(DISTINCT ws_order_number) LIMIT 100