WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics WHERE (cd_marital_status = 'M' AND cd_education_status = 'Advanced Degree') OR (cd_marital_status = 'S' AND cd_education_status = 'College') OR (cd_marital_status = 'W' AND cd_education_status = '2 yr Degree')), filtered_household_demographics AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count IN (1, 3)), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_country = 'United States' AND (ca_state IN ('TX', 'OH', 'TX') OR ca_state IN ('OR', 'NM', 'KY') OR ca_state IN ('VA', 'TX', 'MS'))), branch1 AS (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM store_sales AS ss JOIN filtered_dates AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN filtered_customer_demographics AS cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN filtered_household_demographics AS hd ON ss.ss_hdemo_sk = hd.hd_demo_sk JOIN filtered_customer_address AS ca ON ss.ss_addr_sk = ca.ca_address_sk JOIN store AS s ON ss.ss_store_sk = s.s_store_sk WHERE cd.cd_marital_status = 'M' AND cd.cd_education_status = 'Advanced Degree' AND ss.ss_sales_price BETWEEN 100.00 AND 150.00 AND hd.hd_dep_count = 3 AND ca.ca_state IN ('TX', 'OH', 'TX') AND ss.ss_net_profit BETWEEN 100 AND 200), branch2 AS (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM store_sales AS ss JOIN filtered_dates AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN filtered_customer_demographics AS cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN filtered_household_demographics AS hd ON ss.ss_hdemo_sk = hd.hd_demo_sk JOIN filtered_customer_address AS ca ON ss.ss_addr_sk = ca.ca_address_sk JOIN store AS s ON ss.ss_store_sk = s.s_store_sk WHERE cd.cd_marital_status = 'S' AND cd.cd_education_status = 'College' AND ss.ss_sales_price BETWEEN 50.00 AND 100.00 AND hd.hd_dep_count = 1 AND ca.ca_state IN ('OR', 'NM', 'KY') AND ss.ss_net_profit BETWEEN 150 AND 300), branch3 AS (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost FROM store_sales AS ss JOIN filtered_dates AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN filtered_customer_demographics AS cd ON ss.ss_cdemo_sk = cd.cd_demo_sk JOIN filtered_household_demographics AS hd ON ss.ss_hdemo_sk = hd.hd_demo_sk JOIN filtered_customer_address AS ca ON ss.ss_addr_sk = ca.ca_address_sk JOIN store AS s ON ss.ss_store_sk = s.s_store_sk WHERE cd.cd_marital_status = 'W' AND cd.cd_education_status = '2 yr Degree' AND ss.ss_sales_price BETWEEN 150.00 AND 200.00 AND hd.hd_dep_count = 1 AND ca.ca_state IN ('VA', 'TX', 'MS') AND ss.ss_net_profit BETWEEN 50 AND 250)
SELECT AVG(ss_quantity) AS avg1, AVG(ss_ext_sales_price) AS avg2, AVG(ss_ext_wholesale_cost) AS avg3, SUM(ss_ext_wholesale_cost) FROM (SELECT * FROM branch1 UNION ALL SELECT * FROM branch2 UNION ALL SELECT * FROM branch3) AS all_branches