WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1200 AND 1200 + 11), filtered_catalog_sales AS (SELECT cs.* FROM catalog_sales AS cs JOIN filtered_dates AS fd ON cs.cs_ship_date_sk = fd.d_date_sk)
SELECT SUBSTRING(w.w_warehouse_name, 1, 20) AS w_substr, sm.sm_type, LOWER(cc.cc_name) AS cc_name_lower, SUM(CASE WHEN (cs.cs_ship_date_sk - cs.cs_sold_date_sk <= 30) THEN 1 ELSE 0 END) AS "30 days", SUM(CASE WHEN (cs.cs_ship_date_sk - cs.cs_sold_date_sk > 30) AND (cs.cs_ship_date_sk - cs.cs_sold_date_sk <= 60) THEN 1 ELSE 0 END) AS "31-60 days", SUM(CASE WHEN (cs.cs_ship_date_sk - cs.cs_sold_date_sk > 60) AND (cs.cs_ship_date_sk - cs.cs_sold_date_sk <= 90) THEN 1 ELSE 0 END) AS "61-90 days", SUM(CASE WHEN (cs.cs_ship_date_sk - cs.cs_sold_date_sk > 90) AND (cs.cs_ship_date_sk - cs.cs_sold_date_sk <= 120) THEN 1 ELSE 0 END) AS "91-120 days", SUM(CASE WHEN (cs.cs_ship_date_sk - cs.cs_sold_date_sk > 120) THEN 1 ELSE 0 END) AS ">120 days" FROM filtered_catalog_sales AS cs, warehouse AS w, ship_mode AS sm, call_center AS cc WHERE cs.cs_warehouse_sk = w.w_warehouse_sk AND cs.cs_ship_mode_sk = sm.sm_ship_mode_sk AND cs.cs_call_center_sk = cc.cc_call_center_sk GROUP BY SUBSTRING(w.w_warehouse_name, 1, 20), sm.sm_type, cc.cc_name ORDER BY w_substr NULLS FIRST, sm.sm_type NULLS FIRST, cc_name_lower NULLS FIRST LIMIT 100