WITH date_filtered AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 9), address_filtered AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), music_items AS (SELECT i_item_id, i_item_sk FROM item WHERE i_category = 'Music'), ss AS (SELECT i.i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales AS ss JOIN music_items AS i ON ss.ss_item_sk = i.i_item_sk JOIN date_filtered AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN address_filtered AS a ON ss.ss_addr_sk = a.ca_address_sk GROUP BY i.i_item_id), ws AS (SELECT i.i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales AS ws JOIN music_items AS i ON ws.ws_item_sk = i.i_item_sk JOIN date_filtered AS d ON ws.ws_sold_date_sk = d.d_date_sk JOIN address_filtered AS a ON ws.ws_bill_addr_sk = a.ca_address_sk GROUP BY i.i_item_id), cs AS (SELECT i.i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales AS cs JOIN music_items AS i ON cs.cs_item_sk = i.i_item_sk JOIN date_filtered AS d ON cs.cs_sold_date_sk = d.d_date_sk JOIN address_filtered AS a ON cs.cs_bill_addr_sk = a.ca_address_sk GROUP BY i.i_item_id)
SELECT i_item_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_item_id ORDER BY i_item_id, total_sales LIMIT 100