WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998), filtered_cd1 AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'F' AND cd_education_status = 'Unknown'), filtered_customer AS (SELECT c_customer_sk, c_current_cdemo_sk, c_current_addr_sk, c_birth_year FROM customer WHERE c_birth_month IN (1, 6, 8, 9, 12, 2)), filtered_address AS (SELECT ca_address_sk, ca_country, ca_state, ca_county FROM customer_address WHERE ca_state IN ('MS', 'IN', 'ND', 'OK', 'NM', 'VA', 'MS')), prejoined_facts AS (SELECT cs.cs_item_sk, cs.cs_quantity, cs.cs_list_price, cs.cs_coupon_amt, cs.cs_sales_price, cs.cs_net_profit, cs.cs_bill_customer_sk FROM catalog_sales AS cs JOIN filtered_dates AS fd ON cs.cs_sold_date_sk = fd.d_date_sk JOIN filtered_cd1 AS cd1 ON cs.cs_bill_cdemo_sk = cd1.cd_demo_sk)
SELECT i.i_item_id, fa.ca_country, fa.ca_state, fa.ca_county, AVG(CAST(pjf.cs_quantity AS DECIMAL(12, 2))) AS agg1, AVG(CAST(pjf.cs_list_price AS DECIMAL(12, 2))) AS agg2, AVG(CAST(pjf.cs_coupon_amt AS DECIMAL(12, 2))) AS agg3, AVG(CAST(pjf.cs_sales_price AS DECIMAL(12, 2))) AS agg4, AVG(CAST(pjf.cs_net_profit AS DECIMAL(12, 2))) AS agg5, AVG(CAST(fc.c_birth_year AS DECIMAL(12, 2))) AS agg6, AVG(CAST(cd2.cd_dep_count AS DECIMAL(12, 2))) AS agg7 FROM prejoined_facts AS pjf JOIN filtered_customer AS fc ON pjf.cs_bill_customer_sk = fc.c_customer_sk JOIN filtered_address AS fa ON fc.c_current_addr_sk = fa.ca_address_sk JOIN customer_demographics AS cd2 ON fc.c_current_cdemo_sk = cd2.cd_demo_sk JOIN item AS i ON pjf.cs_item_sk = i.i_item_sk GROUP BY ROLLUP (i.i_item_id, fa.ca_country, fa.ca_state, fa.ca_county) ORDER BY fa.ca_country NULLS FIRST, fa.ca_state NULLS FIRST, fa.ca_county NULLS FIRST, i.i_item_id NULLS FIRST LIMIT 100