WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_month_seq BETWEEN 1200 AND 1211), filtered_sales AS (SELECT ss_net_profit, ss_store_sk, ss_sold_date_sk FROM store_sales), state_ranking AS (SELECT s_state, RANK() OVER (ORDER BY SUM(ss_net_profit) DESC) AS ranking FROM filtered_sales JOIN store ON s_store_sk = ss_store_sk JOIN date_filter ON d_date_sk = ss_sold_date_sk GROUP BY s_state), top_states AS (SELECT s_state FROM state_ranking WHERE ranking <= 5)
SELECT SUM(ss_net_profit) AS total_sum, s_state, s_county, GROUPING(s_state) + GROUPING(s_county) AS lochierarchy, RANK() OVER (PARTITION BY GROUPING(s_state) + GROUPING(s_county), CASE WHEN GROUPING(s_county) = 0 THEN s_state END ORDER BY SUM(ss_net_profit) DESC) AS rank_within_parent FROM filtered_sales JOIN store ON s_store_sk = ss_store_sk JOIN date_filter ON d_date_sk = ss_sold_date_sk WHERE s_state IN (SELECT s_state FROM top_states) GROUP BY ROLLUP (s_state, s_county) ORDER BY lochierarchy DESC, CASE WHEN GROUPING(s_state) + GROUPING(s_county) = 0 THEN s_state END, rank_within_parent LIMIT 100