WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-05-25' AS DATE) AND CAST('2000-07-24' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 62 AND 92 AND i_manufact_id IN (129, 270, 821, 423)), filtered_inventory AS (SELECT inv_item_sk, inv_date_sk FROM inventory WHERE inv_quantity_on_hand BETWEEN 100 AND 500), inventory_with_dates AS (SELECT fi.inv_item_sk, fi.inv_date_sk FROM filtered_inventory AS fi JOIN filtered_dates AS fd ON fi.inv_date_sk = fd.d_date_sk), qualified_items AS (SELECT i.i_item_sk, i.i_item_id, i.i_item_desc, i.i_current_price FROM filtered_items AS i JOIN inventory_with_dates AS iwd ON i.i_item_sk = iwd.inv_item_sk)
SELECT qi.i_item_id, qi.i_item_desc, qi.i_current_price FROM qualified_items AS qi JOIN store_sales AS ss ON qi.i_item_sk = ss.ss_item_sk GROUP BY qi.i_item_id, qi.i_item_desc, qi.i_current_price ORDER BY qi.i_item_id LIMIT 100