WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN CAST('2000-05-25' AS DATE) AND CAST('2000-07-24' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id, i_item_desc, i_current_price FROM item WHERE i_current_price BETWEEN 62 AND 62 + 30 AND i_manufact_id IN (129, 270, 821, 423)), valid_inventory AS (SELECT inv_item_sk FROM inventory JOIN filtered_dates ON d_date_sk = inv_date_sk WHERE inv_quantity_on_hand BETWEEN 100 AND 500)
SELECT i_item_id, i_item_desc, i_current_price FROM filtered_items AS fi JOIN valid_inventory AS vi ON fi.i_item_sk = vi.inv_item_sk WHERE EXISTS(SELECT 1 FROM store_sales WHERE ss_item_sk = fi.i_item_sk) GROUP BY i_item_id, i_item_desc, i_current_price ORDER BY i_item_id LIMIT 100