WITH filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Edgewood'), filtered_income_band AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 38128 AND ib_upper_bound <= 88128), filtered_household_demographics AS (SELECT hd_demo_sk FROM household_demographics AS hd JOIN filtered_income_band AS ib ON hd.hd_income_band_sk = ib.ib_income_band_sk), filtered_customer AS (SELECT c_customer_id, c_last_name, c_first_name, c_current_cdemo_sk, c_current_hdemo_sk FROM customer AS c JOIN filtered_customer_address AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN filtered_household_demographics AS hd ON c.c_current_hdemo_sk = hd.hd_demo_sk), filtered_customer_demographics AS (SELECT cd_demo_sk FROM customer_demographics AS cd JOIN filtered_customer AS c ON cd.cd_demo_sk = c.c_current_cdemo_sk)
SELECT c_customer_id AS customer_id, CONCAT(CONCAT(COALESCE(c_last_name, ''), ', '), COALESCE(c_first_name, '')) AS customername FROM filtered_customer AS c JOIN filtered_customer_demographics AS cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN store_returns AS sr ON sr.sr_cdemo_sk = cd.cd_demo_sk ORDER BY c_customer_id NULLS FIRST LIMIT 100