WITH filtered_dates_d1 AS (SELECT d_date_sk FROM date_dim WHERE d_moy = 9 AND d_year = 1999), filtered_dates_d2 AS (SELECT d_date_sk FROM date_dim WHERE d_moy BETWEEN 9 AND 12 AND d_year = 1999), filtered_dates_d3 AS (SELECT d_date_sk FROM date_dim WHERE d_year IN (1999, 2000, 2001)), filtered_store_sales AS (SELECT ss_quantity, ss_item_sk, ss_store_sk, ss_customer_sk, ss_ticket_number FROM store_sales JOIN filtered_dates_d1 ON ss_sold_date_sk = d_date_sk), filtered_store_returns AS (SELECT sr_return_quantity, sr_customer_sk, sr_item_sk, sr_ticket_number FROM store_returns JOIN filtered_dates_d2 ON sr_returned_date_sk = d_date_sk), filtered_catalog_sales AS (SELECT cs_quantity, cs_bill_customer_sk, cs_item_sk FROM catalog_sales JOIN filtered_dates_d3 ON cs_sold_date_sk = d_date_sk)
SELECT i_item_id, i_item_desc, s_store_id, s_store_name, SUM(ss_quantity) AS store_sales_quantity, SUM(sr_return_quantity) AS store_returns_quantity, SUM(cs_quantity) AS catalog_sales_quantity FROM filtered_store_sales JOIN store ON s_store_sk = ss_store_sk JOIN item ON i_item_sk = ss_item_sk JOIN filtered_store_returns ON (ss_customer_sk = sr_customer_sk AND ss_item_sk = sr_item_sk AND ss_ticket_number = sr_ticket_number) JOIN filtered_catalog_sales ON (sr_customer_sk = cs_bill_customer_sk AND sr_item_sk = cs_item_sk) GROUP BY i_item_id, i_item_desc, s_store_id, s_store_name ORDER BY i_item_id, i_item_desc, s_store_id, s_store_name LIMIT 100