WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001), filtered_customer_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_country = 'United States' AND (ca_state IN ('SD', 'KS', 'MI') OR ca_state IN ('MO', 'ND', 'CO') OR ca_state IN ('NH', 'OH', 'TX'))), filtered_store_sales AS (SELECT ss_quantity, ss_ext_sales_price, ss_ext_wholesale_cost, ss_hdemo_sk, ss_cdemo_sk, ss_addr_sk, ss_store_sk, ss_sales_price, ss_net_profit FROM store_sales WHERE ((ss_hdemo_sk = hd_demo_sk AND cd_demo_sk = ss_cdemo_sk AND cd_marital_status = 'D' AND cd_education_status = 'Unknown' AND ss_sales_price BETWEEN 100.00 AND 150.00 AND hd_dep_count = 3) OR (ss_hdemo_sk = hd_demo_sk AND cd_demo_sk = ss_cdemo_sk AND cd_marital_status = 'S' AND cd_education_status = 'College' AND ss_sales_price BETWEEN 50.00 AND 100.00 AND hd_dep_count = 1) OR (ss_hdemo_sk = hd_demo_sk AND cd_demo_sk = ss_cdemo_sk AND cd_marital_status = 'M' AND cd_education_status = '4 yr Degree' AND ss_sales_price BETWEEN 150.00 AND 200.00 AND hd_dep_count = 1)) AND ((ss_addr_sk = ca_address_sk AND ca_country = 'United States' AND ca_state IN ('SD', 'KS', 'MI') AND ss_net_profit BETWEEN 100 AND 200) OR (ss_addr_sk = ca_address_sk AND ca_country = 'United States' AND ca_state IN ('MO', 'ND', 'CO') AND ss_net_profit BETWEEN 150 AND 300) OR (ss_addr_sk = ca_address_sk AND ca_country = 'United States' AND ca_state IN ('NH', 'OH', 'TX') AND ss_net_profit BETWEEN 50 AND 250)))
SELECT AVG(ss_quantity), AVG(ss_ext_sales_price), AVG(ss_ext_wholesale_cost), SUM(ss_ext_wholesale_cost) FROM filtered_store_sales AS fss JOIN filtered_date AS fd ON fss.ss_sold_date_sk = fd.d_date_sk JOIN store AS s ON s.s_store_sk = fss.ss_store_sk JOIN customer_demographics AS cd ON cd.cd_demo_sk = fss.ss_cdemo_sk JOIN household_demographics AS hd ON hd.hd_demo_sk = fss.ss_hdemo_sk JOIN filtered_customer_address AS fca ON fca.ca_address_sk = fss.ss_addr_sk