WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2002-4-01' AND (CAST('2002-4-01' AS DATE) + INTERVAL '60' DAY)), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_state = 'WV'), filtered_call_center AS (SELECT cc_call_center_sk FROM call_center WHERE cc_county IN ('Ziebach County', 'Luce County', 'Richland County', 'Daviess County', 'Barrow County')), multi_warehouse_orders AS (SELECT DISTINCT cs_order_number FROM catalog_sales GROUP BY cs_order_number HAVING MIN(cs_warehouse_sk) <> MAX(cs_warehouse_sk)), returned_orders AS (SELECT DISTINCT cr_order_number FROM catalog_returns), filtered_sales AS (SELECT cs_order_number, cs_ext_ship_cost, cs_net_profit FROM catalog_sales AS cs1 JOIN filtered_dates ON cs1.cs_ship_date_sk = filtered_dates.d_date_sk JOIN filtered_address ON cs1.cs_ship_addr_sk = filtered_address.ca_address_sk JOIN filtered_call_center ON cs1.cs_call_center_sk = filtered_call_center.cc_call_center_sk)
SELECT COUNT(DISTINCT fs.cs_order_number) AS "order count", SUM(fs.cs_ext_ship_cost) AS "total shipping cost", SUM(fs.cs_net_profit) AS "total net profit" FROM filtered_sales AS fs JOIN multi_warehouse_orders AS mwo ON fs.cs_order_number = mwo.cs_order_number LEFT JOIN returned_orders AS ro ON fs.cs_order_number = ro.cr_order_number WHERE ro.cr_order_number IS NULL ORDER BY COUNT(DISTINCT fs.cs_order_number) LIMIT 100