WITH date_range AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '1999-01-07' AND (CAST('1999-01-07' AS DATE) + INTERVAL '90' DAY)), item_avg_discount AS (SELECT cs_item_sk, 1.3 * AVG(cs_ext_discount_amt) AS avg_discount_threshold FROM catalog_sales JOIN date_range ON cs_sold_date_sk = d_date_sk GROUP BY cs_item_sk)
SELECT SUM(cs.cs_ext_discount_amt) AS "excess discount amount" FROM catalog_sales AS cs JOIN item AS i ON cs.cs_item_sk = i.i_item_sk JOIN date_range AS d ON cs.cs_sold_date_sk = d.d_date_sk JOIN item_avg_discount AS avg ON cs.cs_item_sk = avg.cs_item_sk WHERE i.i_manufact_id = 29 AND cs.cs_ext_discount_amt > avg.avg_discount_threshold LIMIT 100