WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000 AND d_moy = 8), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7), ss AS (SELECT i_item_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_addresses ON ss_addr_sk = ca_address_sk WHERE ss_item_sk = i_item_sk AND i_category IN ('Children') GROUP BY i_item_id), ws AS (SELECT i_item_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales JOIN filtered_dates ON ws_sold_date_sk = d_date_sk JOIN filtered_addresses ON ws_bill_addr_sk = ca_address_sk WHERE ws_item_sk = i_item_sk AND i_category IN ('Children') GROUP BY i_item_id), cs AS (SELECT i_item_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk JOIN filtered_addresses ON cs_bill_addr_sk = ca_address_sk WHERE cs_item_sk = i_item_sk AND i_category IN ('Children') GROUP BY i_item_id)
SELECT i_item_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM cs UNION ALL SELECT * FROM ws) AS tmp1 GROUP BY i_item_id ORDER BY i_item_id, total_sales LIMIT 100