WITH filtered_dates AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_moy = 11), filtered_items AS (SELECT i_item_sk, i_brand_id, i_brand FROM item WHERE i_manufact_id = 816), filtered_sales AS (SELECT dt.d_year, item.i_brand_id, item.i_brand, ss_sales_price FROM store_sales JOIN filtered_dates AS dt ON store_sales.ss_sold_date_sk = dt.d_date_sk JOIN filtered_items AS item ON store_sales.ss_item_sk = item.i_item_sk)
SELECT d_year, i_brand_id AS brand_id, i_brand AS brand, SUM(ss_sales_price) AS sum_agg FROM filtered_sales GROUP BY d_year, i_brand_id, i_brand ORDER BY d_year, sum_agg DESC, brand_id LIMIT 100