WITH store_threshold AS (SELECT AVG(ss_net_profit) AS rank_col FROM store_sales WHERE ss_store_sk = 146 AND ss_addr_sk IS NULL GROUP BY ss_store_sk), item_profits AS (SELECT ss_item_sk AS item_sk, AVG(ss_net_profit) AS avg_profit FROM store_sales WHERE ss_store_sk = 146 GROUP BY ss_item_sk HAVING AVG(ss_net_profit) > 0.9 * (SELECT rank_col FROM store_threshold)), ranked_items AS (SELECT item_sk, RANK() OVER (ORDER BY avg_profit ASC) AS asc_rnk, RANK() OVER (ORDER BY avg_profit DESC) AS desc_rnk FROM item_profits)
SELECT asc_rnk AS rnk, i1.i_product_name AS best_performing, i2.i_product_name AS worst_performing FROM ranked_items JOIN item AS i1 ON i1.i_item_sk = ranked_items.item_sk JOIN item AS i2 ON i2.i_item_sk = ranked_items.item_sk WHERE asc_rnk < 11 AND desc_rnk < 11 AND asc_rnk = desc_rnk ORDER BY asc_rnk LIMIT 100