WITH filtered_income_band AS (SELECT ib_income_band_sk FROM income_band WHERE ib_lower_bound >= 5806 AND ib_upper_bound <= 5806 + 50000), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_city = 'Oakwood'), filtered_customers AS (SELECT c_customer_sk, c_customer_id, c_first_name, c_last_name, c_current_cdemo_sk FROM customer AS c JOIN filtered_address AS a ON c.c_current_addr_sk = a.ca_address_sk JOIN household_demographics AS hd ON c.c_current_hdemo_sk = hd.hd_demo_sk JOIN filtered_income_band AS ib ON hd.hd_income_band_sk = ib.ib_income_band_sk), filtered_cd_keys AS (SELECT DISTINCT c_current_cdemo_sk FROM filtered_customers), filtered_store_returns AS (SELECT sr_cdemo_sk FROM store_returns JOIN filtered_cd_keys ON sr_cdemo_sk = c_current_cdemo_sk)
SELECT c.c_customer_id AS customer_id, COALESCE(c.c_last_name, '') || ', ' || COALESCE(c.c_first_name, '') AS customername FROM filtered_customers AS c JOIN customer_demographics AS cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN filtered_store_returns AS sr ON cd.cd_demo_sk = sr.sr_cdemo_sk ORDER BY c_customer_id LIMIT 100