WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2000), filtered_cd AS (SELECT cd_demo_sk, cd_marital_status, cd_education_status FROM customer_demographics WHERE (cd_marital_status = 'M' AND cd_education_status = '4 yr Degree') OR (cd_marital_status = 'S' AND cd_education_status = 'Secondary') OR (cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree')), filtered_ca AS (SELECT ca_address_sk, ca_country, ca_state FROM customer_address WHERE ca_country = 'United States' AND (ca_state IN ('FL', 'TX', 'DE') OR ca_state IN ('IN', 'ND', 'ID') OR ca_state IN ('MT', 'IL', 'OH')))
SELECT SUBSTRING(r_reason_desc, 1, 20), AVG(ws_quantity), AVG(wr_refunded_cash), AVG(wr_fee) FROM web_sales JOIN filtered_dates ON ws_sold_date_sk = d_date_sk JOIN web_returns ON ws_item_sk = wr_item_sk AND ws_order_number = wr_order_number JOIN filtered_cd AS cd1 ON wr_refunded_cdemo_sk = cd1.cd_demo_sk JOIN filtered_cd AS cd2 ON wr_returning_cdemo_sk = cd2.cd_demo_sk JOIN filtered_ca ON ca_address_sk = wr_refunded_addr_sk JOIN reason ON r_reason_sk = wr_reason_sk JOIN web_page ON ws_web_page_sk = wp_web_page_sk WHERE ((cd1.cd_marital_status = 'M' AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = '4 yr Degree' AND cd1.cd_education_status = cd2.cd_education_status AND ws_sales_price BETWEEN 100.00 AND 150.00) OR (cd1.cd_marital_status = 'S' AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = 'Secondary' AND cd1.cd_education_status = cd2.cd_education_status AND ws_sales_price BETWEEN 50.00 AND 100.00) OR (cd1.cd_marital_status = 'W' AND cd1.cd_marital_status = cd2.cd_marital_status AND cd1.cd_education_status = 'Advanced Degree' AND cd1.cd_education_status = cd2.cd_education_status AND ws_sales_price BETWEEN 150.00 AND 200.00)) AND ((ca_country = 'United States' AND ca_state IN ('FL', 'TX', 'DE') AND ws_net_profit BETWEEN 100 AND 200) OR (ca_country = 'United States' AND ca_state IN ('IN', 'ND', 'ID') AND ws_net_profit BETWEEN 150 AND 300) OR (ca_country = 'United States' AND ca_state IN ('MT', 'IL', 'OH') AND ws_net_profit BETWEEN 50 AND 250)) GROUP BY r_reason_desc ORDER BY SUBSTRING(r_reason_desc, 1, 20), AVG(ws_quantity), AVG(wr_refunded_cash), AVG(wr_fee) LIMIT 100