WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year IN (1999, 2000, 2001)), filtered_store AS (SELECT s_store_sk FROM store WHERE s_county = 'Williamson County'), filtered_hd AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_vehicle_count > 0 AND (CASE WHEN hd_vehicle_count > 0 THEN (hd_dep_count * 1.000) / hd_vehicle_count ELSE NULL END) > 1.2), sales_branch_1 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 1 AND 3 AND hd_buy_potential = '>10000' GROUP BY ss_ticket_number, ss_customer_sk), sales_branch_2 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 1 AND 3 AND hd_buy_potential = 'Unknown' GROUP BY ss_ticket_number, ss_customer_sk), sales_branch_3 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 25 AND 28 AND hd_buy_potential = '>10000' GROUP BY ss_ticket_number, ss_customer_sk), sales_branch_4 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd ON ss_hdemo_sk = hd_demo_sk WHERE d_dom BETWEEN 25 AND 28 AND hd_buy_potential = 'Unknown' GROUP BY ss_ticket_number, ss_customer_sk), combined_sales AS (SELECT ss_ticket_number, ss_customer_sk, cnt FROM sales_branch_1 UNION ALL SELECT ss_ticket_number, ss_customer_sk, cnt FROM sales_branch_2 UNION ALL SELECT ss_ticket_number, ss_customer_sk, cnt FROM sales_branch_3 UNION ALL SELECT ss_ticket_number, ss_customer_sk, cnt FROM sales_branch_4)
SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM combined_sales AS dn, customer WHERE ss_customer_sk = c_customer_sk AND cnt BETWEEN 15 AND 20 ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, c_salutation NULLS FIRST, c_preferred_cust_flag DESC NULLS FIRST, ss_ticket_number NULLS FIRST