WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 11), filtered_addresses AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -7), filtered_households AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential LIKE 'Unknown%'), branch_1 AS (SELECT cc_call_center_id, cc_name, cc_manager, cr_net_loss, cd_marital_status, cd_education_status FROM catalog_returns JOIN filtered_dates ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk JOIN filtered_households ON hd_demo_sk = c_current_hdemo_sk JOIN filtered_addresses ON ca_address_sk = c_current_addr_sk JOIN call_center ON cr_call_center_sk = cc_call_center_sk WHERE cd_marital_status = 'M' AND cd_education_status = 'Unknown'), branch_2 AS (SELECT cc_call_center_id, cc_name, cc_manager, cr_net_loss, cd_marital_status, cd_education_status FROM catalog_returns JOIN filtered_dates ON cr_returned_date_sk = d_date_sk JOIN customer ON cr_returning_customer_sk = c_customer_sk JOIN customer_demographics ON cd_demo_sk = c_current_cdemo_sk JOIN filtered_households ON hd_demo_sk = c_current_hdemo_sk JOIN filtered_addresses ON ca_address_sk = c_current_addr_sk JOIN call_center ON cr_call_center_sk = cc_call_center_sk WHERE cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree'), combined_sales AS (SELECT * FROM branch_1 UNION ALL SELECT * FROM branch_2)
SELECT cc_call_center_id AS Call_Center, cc_name AS Call_Center_Name, cc_manager AS Manager, SUM(cr_net_loss) AS Returns_Loss FROM combined_sales GROUP BY cc_call_center_id, cc_name, cc_manager, cd_marital_status, cd_education_status ORDER BY SUM(cr_net_loss) DESC