WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2002), filtered_store AS (SELECT s_store_sk FROM store WHERE s_state = 'TN'), filtered_cdemo AS (SELECT cd_demo_sk FROM customer_demographics WHERE cd_gender = 'M' AND cd_marital_status = 'S' AND cd_education_status = 'College'), filtered_sales AS (SELECT ss.ss_item_sk, ss.ss_store_sk, ss.ss_quantity, ss.ss_list_price, ss.ss_coupon_amt, ss.ss_sales_price FROM store_sales AS ss JOIN filtered_date AS d ON ss.ss_sold_date_sk = d.d_date_sk JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN filtered_cdemo AS cd ON ss.ss_cdemo_sk = cd.cd_demo_sk), results AS (SELECT i.i_item_id, 'TN' AS s_state, 0 AS g_state, f.ss_quantity AS agg1, f.ss_list_price AS agg2, f.ss_coupon_amt AS agg3, f.ss_sales_price AS agg4 FROM filtered_sales AS f JOIN item AS i ON f.ss_item_sk = i.i_item_sk)
SELECT i_item_id, s_state, g_state, agg1, agg2, agg3, agg4 FROM (SELECT i_item_id, s_state, 0 AS g_state, AVG(agg1) AS agg1, AVG(agg2) AS agg2, AVG(agg3) AS agg3, AVG(agg4) AS agg4 FROM results GROUP BY i_item_id, s_state UNION ALL SELECT i_item_id, NULL AS s_state, 1 AS g_state, AVG(agg1) AS agg1, AVG(agg2) AS agg2, AVG(agg3) AS agg3, AVG(agg4) AS agg4 FROM results GROUP BY i_item_id UNION ALL SELECT NULL AS i_item_id, NULL AS s_state, 1 AS g_state, AVG(agg1) AS agg1, AVG(agg2) AS agg2, AVG(agg3) AS agg3, AVG(agg4) AS agg4 FROM results) AS foo ORDER BY i_item_id NULLS FIRST, s_state NULLS FIRST LIMIT 100