WITH filtered_dates AS (SELECT d_date_sk, d_year FROM date_dim WHERE d_moy = 11), filtered_items AS (SELECT i_item_sk, i_brand_id, i_brand FROM item WHERE i_manufact_id = 128), joined_sales AS (SELECT ss_ext_sales_price, ss_sold_date_sk, ss_item_sk FROM store_sales), filtered_sales AS (SELECT fd.d_year, fi.i_brand_id, fi.i_brand, js.ss_ext_sales_price FROM joined_sales AS js JOIN filtered_dates AS fd ON js.ss_sold_date_sk = fd.d_date_sk JOIN filtered_items AS fi ON js.ss_item_sk = fi.i_item_sk)
SELECT d_year, i_brand_id AS brand_id, i_brand AS brand, SUM(ss_ext_sales_price) AS sum_agg FROM filtered_sales GROUP BY d_year, i_brand, i_brand_id ORDER BY d_year, sum_agg DESC, brand_id LIMIT 100