WITH electronics_manufacturers AS (SELECT DISTINCT i_manufact_id FROM item WHERE i_category IN ('Electronics')), filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 5), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), ss AS (SELECT ei.i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales AS ss JOIN electronics_manufacturers AS em ON ss.ss_item_sk = (SELECT i_item_sk FROM item AS i WHERE i.i_manufact_id = em.i_manufact_id AND i.i_category IN ('Electronics') LIMIT 1) JOIN filtered_dates AS fd ON ss.ss_sold_date_sk = fd.d_date_sk JOIN filtered_address AS fa ON ss.ss_addr_sk = fa.ca_address_sk GROUP BY ei.i_manufact_id), ws AS (SELECT ei.i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales AS ws JOIN electronics_manufacturers AS em ON ws.ws_item_sk = (SELECT i_item_sk FROM item AS i WHERE i.i_manufact_id = em.i_manufact_id AND i.i_category IN ('Electronics') LIMIT 1) JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk JOIN filtered_address AS fa ON ws.ws_bill_addr_sk = fa.ca_address_sk GROUP BY ei.i_manufact_id), cs AS (SELECT ei.i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales AS cs JOIN electronics_manufacturers AS em ON cs.cs_item_sk = (SELECT i_item_sk FROM item AS i WHERE i.i_manufact_id = em.i_manufact_id AND i.i_category IN ('Electronics') LIMIT 1) JOIN filtered_dates AS fd ON cs.cs_sold_date_sk = fd.d_date_sk JOIN filtered_address AS fa ON cs.cs_bill_addr_sk = fa.ca_address_sk GROUP BY ei.i_manufact_id)
SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM ws UNION ALL SELECT * FROM cs) AS tmp1 GROUP BY i_manufact_id ORDER BY total_sales LIMIT 100