WITH filtered_dates AS (SELECT d_date_sk, d_date FROM date_dim WHERE d_date BETWEEN CAST('2000-02-10' AS DATE) AND CAST('2000-04-10' AS DATE)), filtered_items AS (SELECT i_item_sk, i_item_id FROM item WHERE i_current_price BETWEEN 0.99 AND 1.49), filtered_catalog_sales AS (SELECT cs_order_number, cs_item_sk, cs_warehouse_sk, cs_sales_price, d_date FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk), filtered_sales_with_items AS (SELECT fcs.*, fi.i_item_id FROM filtered_catalog_sales AS fcs JOIN filtered_items AS fi ON fcs.cs_item_sk = fi.i_item_sk), sales_with_warehouse AS (SELECT fsi.cs_order_number, fsi.cs_item_sk, fsi.cs_sales_price, fsi.d_date, fsi.i_item_id, w.w_state FROM filtered_sales_with_items AS fsi JOIN warehouse AS w ON fsi.cs_warehouse_sk = w.w_warehouse_sk)
SELECT sw.w_state, sw.i_item_id, SUM(CASE WHEN (CAST(sw.d_date AS DATE) < CAST('2000-03-11' AS DATE)) THEN sw.cs_sales_price - COALESCE(cr.cr_refunded_cash, 0) ELSE 0 END) AS sales_before, SUM(CASE WHEN (CAST(sw.d_date AS DATE) >= CAST('2000-03-11' AS DATE)) THEN sw.cs_sales_price - COALESCE(cr.cr_refunded_cash, 0) ELSE 0 END) AS sales_after FROM sales_with_warehouse AS sw LEFT OUTER JOIN catalog_returns AS cr ON sw.cs_order_number = cr.cr_order_number AND sw.cs_item_sk = cr.cr_item_sk GROUP BY sw.w_state, sw.i_item_id ORDER BY sw.w_state, sw.i_item_id LIMIT 100