WITH date_filter AS (SELECT d_date_sk FROM date_dim WHERE d_dow IN (6, 0) AND d_year IN (1999, 2000, 2001)), store_filter AS (SELECT s_store_sk FROM store WHERE s_city IN ('Fairview', 'Midway')), hd_dep4 AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count = 4), hd_vehicle3 AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_vehicle_count = 3), branch1_sales AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ss_coupon_amt, ss_net_profit FROM store_sales JOIN date_filter ON ss_sold_date_sk = d_date_sk JOIN store_filter ON ss_store_sk = s_store_sk JOIN hd_dep4 ON ss_hdemo_sk = hd_demo_sk), branch2_sales AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ss_coupon_amt, ss_net_profit FROM store_sales JOIN date_filter ON ss_sold_date_sk = d_date_sk JOIN store_filter ON ss_store_sk = s_store_sk JOIN hd_vehicle3 ON ss_hdemo_sk = hd_demo_sk), unified_sales AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ss_coupon_amt, ss_net_profit FROM branch1_sales UNION ALL SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ss_coupon_amt, ss_net_profit FROM branch2_sales), dn AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ca_city AS bought_city, SUM(ss_coupon_amt) AS amt, SUM(ss_net_profit) AS profit FROM unified_sales JOIN customer_address ON ss_addr_sk = ca_address_sk GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, ca_city)
SELECT c_last_name, c_first_name, ca_city, bought_city, ss_ticket_number, amt, profit FROM dn, customer, customer_address AS current_addr WHERE ss_customer_sk = c_customer_sk AND customer.c_current_addr_sk = current_addr.ca_address_sk AND current_addr.ca_city <> bought_city ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, ca_city NULLS FIRST, bought_city NULLS FIRST, ss_ticket_number NULLS FIRST LIMIT 100