WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy = 12), web_sales_filtered AS (SELECT ws.ws_item_sk, ws.ws_order_number, ws.ws_quantity, ws.ws_net_paid, ws.ws_net_profit FROM web_sales AS ws JOIN filtered_dates AS fd ON ws.ws_sold_date_sk = fd.d_date_sk WHERE ws.ws_net_profit > 1 AND ws.ws_net_paid > 0 AND ws.ws_quantity > 0), catalog_sales_filtered AS (SELECT cs.cs_item_sk, cs.cs_order_number, cs.cs_quantity, cs.cs_net_paid, cs.cs_net_profit FROM catalog_sales AS cs JOIN filtered_dates AS fd ON cs.cs_sold_date_sk = fd.d_date_sk WHERE cs.cs_net_profit > 1 AND cs.cs_net_paid > 0 AND cs.cs_quantity > 0), store_sales_filtered AS (SELECT sts.ss_item_sk, sts.ss_ticket_number, sts.ss_quantity, sts.ss_net_paid, sts.ss_net_profit FROM store_sales AS sts JOIN filtered_dates AS fd ON sts.ss_sold_date_sk = fd.d_date_sk WHERE sts.ss_net_profit > 1 AND sts.ss_net_paid > 0 AND sts.ss_quantity > 0), web_returns_filtered AS (SELECT wr.wr_order_number, wr.wr_item_sk, wr.wr_return_quantity, wr.wr_return_amt FROM web_returns AS wr WHERE wr.wr_return_amt > 10000), catalog_returns_filtered AS (SELECT cr.cr_order_number, cr.cr_item_sk, cr.cr_return_quantity, cr.cr_return_amount FROM catalog_returns AS cr WHERE cr.cr_return_amount > 10000), store_returns_filtered AS (SELECT sr.sr_ticket_number, sr.sr_item_sk, sr.sr_return_quantity, sr.sr_return_amt FROM store_returns AS sr WHERE sr.sr_return_amt > 10000), web_channel AS (SELECT 'web' AS channel, ws.ws_item_sk AS item, (CAST(SUM(COALESCE(wr.wr_return_quantity, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(ws.ws_quantity, 0)) AS DECIMAL(15, 4))) AS return_ratio, (CAST(SUM(COALESCE(wr.wr_return_amt, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(ws.ws_net_paid, 0)) AS DECIMAL(15, 4))) AS currency_ratio FROM web_sales_filtered AS ws LEFT OUTER JOIN web_returns_filtered AS wr ON (ws.ws_order_number = wr.wr_order_number AND ws.ws_item_sk = wr.wr_item_sk) GROUP BY ws.ws_item_sk), catalog_channel AS (SELECT 'catalog' AS channel, cs.cs_item_sk AS item, (CAST(SUM(COALESCE(cr.cr_return_quantity, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(cs.cs_quantity, 0)) AS DECIMAL(15, 4))) AS return_ratio, (CAST(SUM(COALESCE(cr.cr_return_amount, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(cs.cs_net_paid, 0)) AS DECIMAL(15, 4))) AS currency_ratio FROM catalog_sales_filtered AS cs LEFT OUTER JOIN catalog_returns_filtered AS cr ON (cs.cs_order_number = cr.cr_order_number AND cs.cs_item_sk = cr.cr_item_sk) GROUP BY cs.cs_item_sk), store_channel AS (SELECT 'store' AS channel, sts.ss_item_sk AS item, (CAST(SUM(COALESCE(sr.sr_return_quantity, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(sts.ss_quantity, 0)) AS DECIMAL(15, 4))) AS return_ratio, (CAST(SUM(COALESCE(sr.sr_return_amt, 0)) AS DECIMAL(15, 4)) / CAST(SUM(COALESCE(sts.ss_net_paid, 0)) AS DECIMAL(15, 4))) AS currency_ratio FROM store_sales_filtered AS sts LEFT OUTER JOIN store_returns_filtered AS sr ON (sts.ss_ticket_number = sr.sr_ticket_number AND sts.ss_item_sk = sr.sr_item_sk) GROUP BY sts.ss_item_sk)
SELECT channel, item, return_ratio, RANK() OVER (PARTITION BY channel ORDER BY return_ratio) AS return_rank, RANK() OVER (PARTITION BY channel ORDER BY currency_ratio) AS currency_rank FROM (SELECT * FROM web_channel UNION ALL SELECT * FROM catalog_channel UNION ALL SELECT * FROM store_channel) AS all_channels WHERE return_rank <= 10 OR currency_rank <= 10 ORDER BY channel NULLS FIRST, return_rank NULLS FIRST, currency_rank NULLS FIRST, item NULLS FIRST LIMIT 100