WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 1 AND 2 AND d_year IN (1999, 2000, 2001)), filtered_store AS (SELECT s_store_sk FROM store WHERE s_city IN ('Fairview', 'Midway')), filtered_hd_dep AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_dep_count = 4), filtered_hd_vehicle AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_vehicle_count = 3), sales_union AS (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ss_ext_sales_price, ss_ext_list_price, ss_ext_tax, ca_city AS bought_city FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd_dep ON ss_hdemo_sk = hd_demo_sk JOIN customer_address ON ss_addr_sk = ca_address_sk UNION ALL SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, ss_ext_sales_price, ss_ext_list_price, ss_ext_tax, ca_city AS bought_city FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN filtered_store ON ss_store_sk = s_store_sk JOIN filtered_hd_vehicle ON ss_hdemo_sk = hd_demo_sk JOIN customer_address ON ss_addr_sk = ca_address_sk)
SELECT c_last_name, c_first_name, current_addr.ca_city, dn.bought_city, dn.ss_ticket_number, dn.extended_price, dn.extended_tax, dn.list_price FROM (SELECT ss_ticket_number, ss_customer_sk, ss_addr_sk, bought_city, SUM(ss_ext_sales_price) AS extended_price, SUM(ss_ext_list_price) AS list_price, SUM(ss_ext_tax) AS extended_tax FROM sales_union GROUP BY ss_ticket_number, ss_customer_sk, ss_addr_sk, bought_city) AS dn, customer, customer_address AS current_addr WHERE dn.ss_customer_sk = c_customer_sk AND customer.c_current_addr_sk = current_addr.ca_address_sk AND current_addr.ca_city <> dn.bought_city ORDER BY c_last_name NULLS FIRST, ss_ticket_number NULLS FIRST LIMIT 100