WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_date BETWEEN '2000-01-27' AND CAST('2000-04-26' AS DATE)), item_avg_discounts AS (SELECT ws_item_sk, 1.3 * AVG(ws_ext_discount_amt) AS threshold_discount FROM web_sales JOIN filtered_dates ON d_date_sk = ws_sold_date_sk GROUP BY ws_item_sk)
SELECT SUM(ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales JOIN filtered_dates ON d_date_sk = ws_sold_date_sk JOIN item ON i_item_sk = ws_item_sk JOIN item_avg_discounts AS iad ON web_sales.ws_item_sk = iad.ws_item_sk WHERE i_manufact_id = 350 AND ws_ext_discount_amt > iad.threshold_discount ORDER BY SUM(ws_ext_discount_amt) LIMIT 100