WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 2001 AND d_moy BETWEEN 4 AND 6), store_sales_customers AS (SELECT DISTINCT ss_customer_sk FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk), web_sales_customers AS (SELECT DISTINCT ws_bill_customer_sk FROM web_sales JOIN filtered_dates ON ws_sold_date_sk = d_date_sk), catalog_sales_customers AS (SELECT DISTINCT cs_ship_customer_sk FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk), filtered_ca AS (SELECT ca_address_sk FROM customer_address WHERE ca_state IN ('KY', 'GA', 'NM'))
SELECT cd_gender, cd_marital_status, cd_education_status, COUNT(*) AS cnt1, cd_purchase_estimate, COUNT(*) AS cnt2, cd_credit_rating, COUNT(*) AS cnt3 FROM customer_demographics AS cd JOIN customer AS c ON cd.cd_demo_sk = c.c_current_cdemo_sk JOIN filtered_ca AS ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store_sales_customers AS ssc ON c.c_customer_sk = ssc.ss_customer_sk LEFT JOIN web_sales_customers AS wsc ON c.c_customer_sk = wsc.ws_bill_customer_sk LEFT JOIN catalog_sales_customers AS csc ON c.c_customer_sk = csc.cs_ship_customer_sk WHERE wsc.ws_bill_customer_sk IS NULL AND csc.cs_ship_customer_sk IS NULL GROUP BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating ORDER BY cd_gender, cd_marital_status, cd_education_status, cd_purchase_estimate, cd_credit_rating LIMIT 100