WITH filtered_date AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 5), filtered_ca AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), filtered_item AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_category IN ('Electronics')), ss AS (SELECT i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales INNER JOIN filtered_date ON store_sales.ss_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_ca ON store_sales.ss_addr_sk = filtered_ca.ca_address_sk INNER JOIN filtered_item ON store_sales.ss_item_sk = filtered_item.i_item_sk GROUP BY i_manufact_id), ws AS (SELECT i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales INNER JOIN filtered_date ON web_sales.ws_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_ca ON web_sales.ws_bill_addr_sk = filtered_ca.ca_address_sk INNER JOIN filtered_item ON web_sales.ws_item_sk = filtered_item.i_item_sk GROUP BY i_manufact_id), cs AS (SELECT i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales INNER JOIN filtered_date ON catalog_sales.cs_sold_date_sk = filtered_date.d_date_sk INNER JOIN filtered_ca ON catalog_sales.cs_bill_addr_sk = filtered_ca.ca_address_sk INNER JOIN filtered_item ON catalog_sales.cs_item_sk = filtered_item.i_item_sk GROUP BY i_manufact_id)
SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM ws UNION ALL SELECT * FROM cs) AS tmp1 GROUP BY i_manufact_id ORDER BY total_sales LIMIT 100