WITH filtered_dates AS (SELECT d_date_sk FROM date_dim WHERE d_year = 1998 AND d_moy = 5), filtered_address AS (SELECT ca_address_sk FROM customer_address WHERE ca_gmt_offset = -5), electronics_items AS (SELECT i_manufact_id, i_item_sk FROM item WHERE i_category IN ('Electronics')), ss AS (SELECT i_manufact_id, SUM(ss_ext_sales_price) AS total_sales FROM store_sales JOIN filtered_dates ON ss_sold_date_sk = d_date_sk JOIN electronics_items ON ss_item_sk = i_item_sk JOIN filtered_address ON ss_addr_sk = ca_address_sk GROUP BY i_manufact_id), ws AS (SELECT i_manufact_id, SUM(ws_ext_sales_price) AS total_sales FROM web_sales JOIN filtered_dates ON ws_sold_date_sk = d_date_sk JOIN electronics_items ON ws_item_sk = i_item_sk JOIN filtered_address ON ws_bill_addr_sk = ca_address_sk GROUP BY i_manufact_id), cs AS (SELECT i_manufact_id, SUM(cs_ext_sales_price) AS total_sales FROM catalog_sales JOIN filtered_dates ON cs_sold_date_sk = d_date_sk JOIN electronics_items ON cs_item_sk = i_item_sk JOIN filtered_address ON cs_bill_addr_sk = ca_address_sk GROUP BY i_manufact_id)
SELECT i_manufact_id, SUM(total_sales) AS total_sales FROM (SELECT * FROM ss UNION ALL SELECT * FROM ws UNION ALL SELECT * FROM cs) AS tmp1 GROUP BY i_manufact_id ORDER BY total_sales LIMIT 100