WITH filtered_dates_dom_1_3 AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 1 AND 3 AND d_year IN (1999, 2000, 2001)), filtered_dates_dom_25_28 AS (SELECT d_date_sk FROM date_dim WHERE d_dom BETWEEN 25 AND 28 AND d_year IN (1999, 2000, 2001)), filtered_store AS (SELECT s_store_sk FROM store WHERE s_county = 'Williamson County'), filtered_hd_buy_potential_gt10000 AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential = '>10000' AND hd_vehicle_count > 0 AND (hd_dep_count * 1.000) / hd_vehicle_count > 1.2), filtered_hd_buy_potential_unknown AS (SELECT hd_demo_sk FROM household_demographics WHERE hd_buy_potential = 'Unknown' AND hd_vehicle_count > 0 AND (hd_dep_count * 1.000) / hd_vehicle_count > 1.2), store_sales_part1 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_dates_dom_1_3 AS dd1 ON ss.ss_sold_date_sk = dd1.d_date_sk JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN filtered_hd_buy_potential_gt10000 AS hd1 ON ss.ss_hdemo_sk = hd1.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk UNION ALL SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_dates_dom_1_3 AS dd1 ON ss.ss_sold_date_sk = dd1.d_date_sk JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN filtered_hd_buy_potential_unknown AS hd2 ON ss.ss_hdemo_sk = hd2.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk), store_sales_part2 AS (SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_dates_dom_25_28 AS dd2 ON ss.ss_sold_date_sk = dd2.d_date_sk JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN filtered_hd_buy_potential_gt10000 AS hd1 ON ss.ss_hdemo_sk = hd1.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk UNION ALL SELECT ss_ticket_number, ss_customer_sk, COUNT(*) AS cnt FROM store_sales AS ss JOIN filtered_dates_dom_25_28 AS dd2 ON ss.ss_sold_date_sk = dd2.d_date_sk JOIN filtered_store AS s ON ss.ss_store_sk = s.s_store_sk JOIN filtered_hd_buy_potential_unknown AS hd2 ON ss.ss_hdemo_sk = hd2.hd_demo_sk GROUP BY ss_ticket_number, ss_customer_sk), dn AS (SELECT ss_ticket_number, ss_customer_sk, SUM(cnt) AS cnt FROM (SELECT ss_ticket_number, ss_customer_sk, cnt FROM store_sales_part1 UNION ALL SELECT ss_ticket_number, ss_customer_sk, cnt FROM store_sales_part2) AS t GROUP BY ss_ticket_number, ss_customer_sk)
SELECT c_last_name, c_first_name, c_salutation, c_preferred_cust_flag, ss_ticket_number, cnt FROM dn JOIN customer AS c ON dn.ss_customer_sk = c.c_customer_sk WHERE cnt BETWEEN 15 AND 20 ORDER BY c_last_name NULLS FIRST, c_first_name NULLS FIRST, c_salutation NULLS FIRST, c_preferred_cust_flag DESC NULLS FIRST, ss_ticket_number NULLS FIRST