WITH manufacturer_items AS (SELECT DISTINCT i1.i_product_name FROM item AS i1 WHERE i1.i_manufact_id BETWEEN 738 AND 738 + 40), matching_items_branch1 AS (SELECT i_manufact FROM item WHERE ((i_category = 'Women' AND i_color IN ('powder', 'khaki') AND i_units IN ('Ounce', 'Oz') AND i_size IN ('medium', 'extra large')) OR (i_category = 'Women' AND i_color IN ('brown', 'honeydew') AND i_units IN ('Bunch', 'Ton') AND i_size IN ('N/A', 'small')) OR (i_category = 'Men' AND i_color IN ('floral', 'deep') AND i_units IN ('N/A', 'Dozen') AND i_size IN ('petite', 'petite')) OR (i_category = 'Men' AND i_color IN ('light', 'cornflower') AND i_units IN ('Box', 'Pound') AND i_size IN ('medium', 'extra large')))), matching_items_branch2 AS (SELECT i_manufact FROM item WHERE ((i_category = 'Women' AND i_color IN ('midnight', 'snow') AND i_units IN ('Pallet', 'Gross') AND i_size IN ('medium', 'extra large')) OR (i_category = 'Women' AND i_color IN ('cyan', 'papaya') AND i_units IN ('Cup', 'Dram') AND i_size IN ('N/A', 'small')) OR (i_category = 'Men' AND i_color IN ('orange', 'frosted') AND i_units IN ('Each', 'Tbl') AND i_size IN ('petite', 'petite')) OR (i_category = 'Men' AND i_color IN ('forest', 'ghost') AND i_units IN ('Lb', 'Bundle') AND i_size IN ('medium', 'extra large')))), matching_manufacturers AS (SELECT DISTINCT i_manufact FROM (SELECT i_manufact FROM matching_items_branch1 UNION ALL SELECT i_manufact FROM matching_items_branch2) AS combined)
SELECT DISTINCT mi.i_product_name FROM manufacturer_items AS mi JOIN matching_manufacturers AS mm ON mi.i_manufact_id = mm.i_manufact ORDER BY mi.i_product_name LIMIT 100