WITH filtered_dates AS (SELECT d_date_sk, d_qoy FROM date_dim WHERE d_month_seq IN (1200, 1201, 1202, 1203, 1204, 1205, 1206, 1207, 1208, 1209, 1210, 1211)), filtered_store_sales AS (SELECT ss_item_sk, ss_store_sk, ss_sales_price, d_qoy FROM store_sales JOIN filtered_dates ON store_sales.ss_sold_date_sk = filtered_dates.d_date_sk), item_branch1 AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_category IN ('Books', 'Children', 'Electronics') AND i_class IN ('personal', 'portable', 'reference', 'self-help') AND i_brand IN ('scholaramalgamalg #14', 'scholaramalgamalg #7', 'exportiunivamalg #9', 'scholaramalgamalg #9')), item_branch2 AS (SELECT i_item_sk, i_manufact_id FROM item WHERE i_category IN ('Women', 'Music', 'Men') AND i_class IN ('accessories', 'classical', 'fragrances', 'pants') AND i_brand IN ('amalgimporto #1', 'edu packscholar #1', 'exportiimporto #1', 'importoamalg #1')), joined_branch1 AS (SELECT fss.d_qoy, i.i_manufact_id, fss.ss_sales_price, s.s_store_sk FROM filtered_store_sales AS fss JOIN item_branch1 AS i ON fss.ss_item_sk = i.i_item_sk JOIN store AS s ON fss.ss_store_sk = s.s_store_sk), joined_branch2 AS (SELECT fss.d_qoy, i.i_manufact_id, fss.ss_sales_price, s.s_store_sk FROM filtered_store_sales AS fss JOIN item_branch2 AS i ON fss.ss_item_sk = i.i_item_sk JOIN store AS s ON fss.ss_store_sk = s.s_store_sk), all_sales AS (SELECT d_qoy, i_manufact_id, ss_sales_price FROM joined_branch1 UNION ALL SELECT d_qoy, i_manufact_id, ss_sales_price FROM joined_branch2), grouped_sales AS (SELECT i_manufact_id, d_qoy, SUM(ss_sales_price) AS sum_sales FROM all_sales GROUP BY i_manufact_id, d_qoy), windowed_sales AS (SELECT i_manufact_id, d_qoy, sum_sales, AVG(sum_sales) OVER (PARTITION BY i_manufact_id) AS avg_quarterly_sales FROM grouped_sales)
SELECT i_manufact_id, sum_sales, avg_quarterly_sales FROM windowed_sales WHERE CASE WHEN avg_quarterly_sales > 0 THEN ABS(sum_sales - avg_quarterly_sales) / avg_quarterly_sales ELSE NULL END > 0.1 ORDER BY avg_quarterly_sales, sum_sales, i_manufact_id LIMIT 100